import Button from '../Button/button';
import PinCard from '../PinCard/PinCard';
import './PinsList.css'

const { useState,useEffect } = require("react")

const PinsList = () => {
    const [pins, setPins] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [pinName, setPinName] = useState();
    const [pinUrl, setPinUrl] = useState();

    /*const [formData, setFormData] = useState({
        title: undefined,
        author: undefined,
        description: undefined,
    });*/

    useEffect(() => {
        const url = "http://localhost:5000/pins"
        fetch(url)
        .then (res => res.json())
        .then((json) => setPins(json))
        .catch((err) => console.log(err));
    }, [refresh]);

    const body = {
        name: pinName,
        user: 74,
        imgUrl: pinUrl,
        numberOfLikes: 8,
        description: "kdnfs",
        url: "",
        id: 55
    }

    const createPin = () => {
        fetch("http://localhost:5000/pins", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/jason'
            },
            body: JSON.stringify(body)
        })
        .then (res => res.json())
        .then((json) => setRefresh(!refresh))
        .catch((err) => console.log(err));
    };

    return (
        <div className="main">
            <div className="heather_pins_list">
                <span className= "pinsList_tittle"> Pins</span>

            </div>
            <div className="board">
                {pins.map((pin)=> <PinCard pin={pin} key={pin.id}/>)}

            </div>
            <div>
                <h3>Crear Pin</h3>
                <input placeholder="Nombre del Pin" onChange={(event) => setPinName(event.target.value)}></input>
                <input placeholder="Url de la foto" onChange={(event) => setPinUrl(event.target.value)}></input>
                <button onClick={() => createPin()}>Create</button>
            </div>   
        </div>
    );
};

export default PinsList;
