import PinCard from '../PinCard/PinCard';
import './PinsList.css'

const { useState,useEffect } = require("react")

const PinsList = () => {
    const [pins, setPins] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [pinName, setPinName] = useState();

    const [formData, setFormData] = useState({
        title: undefined,
        author: undefined,
        description: undefined,
    });

    useEffect(() => {
        const url = "http://localhost:5000/pins"
        fetch(url)
        .then (res => res.json())
        .then((json) => setPins(json))
        .catch((err) => console.log(err));
    }, [refresh]);

    const body = {
        name: pinName,
        user: 74,
        imgUrl: "https://spoiler.bolavip.com/__export/1605137451536/sites/bolavip/img/2020/11/11/leonardo_di_caprio_memes_crop1605135235163.jpg_1693159006.jpg",
        numberOfLikes: 8,
        description: "kdnfs",
        url: "",
        id: 55
    }

    const createPin = () => {
        fetch("http://localhost:5000/pins", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/jason'
            },
            body: JSON.stringify(body)
        })
        .then (res => res.json())
        .then((json) => setRefresh(!refresh))
        .catch((err) => console.log(err));
    };

    return (
        <div className="board">
            <span className= "pinsList_tittle"> Pins</span>
            {pins.map((pin)=> <PinCard pin={pin} key={pin.id}/>)}
            <input onChange={(event) => setPinName(event.target.value)}></input>
            <button onClick={() => createPin()}>Create</button>
         </div>
    );
};

export default PinsList;
